INSERT INTO `job` VALUES ('223146', 'QINGHUA', '【实习】【图灵机器人】自然语言处理 /机器人视觉', '&nbsp;&nbsp;<br/>图灵机器人，人工智能机器人领域最领先的创业公司之一，B轮，估值10亿级，北京上地硅谷亮城——&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>我们是人工智能机器人领域最执着的团队，&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>有草莽英雄，也有来自MIT的技术大牛。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>我们希望机器人不只是替代我们的工作，&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>而是让我们每一个人的生活变得更美好。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>我们的产品是图灵机器人和Turing OS，&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>变态般的努力只为让智能机器人走进每个家庭。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>我们正处于人工智能机器人的风口浪尖，&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>异常地务实，同时又不缺乏理想和情怀。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>一、自然语言处理实习生（数据处理方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位名称】自然语言处理实习生（数据处理方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【所属小组】语义组，主要进行数据清洗等相关工作&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【人数】1~2人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职责描述】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.数据清洗，清除无效信息，合并重复信息，保证数据有效性&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.数据分析，从不同维度挖掘分析数据，寻找数据间的相关性，并以最合理的形式展示&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3.反馈收集，收集用户需求，并跟踪处理&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位要求】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.对数字敏感，精通办公软件，善于挖掘分析数据，强逻辑思维&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.踏实、认真、负责、热情、强烈的好奇心&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3.善于表达，无沟通障碍，能够协调团队合作&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>二、自然语言处理实习生（语义方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位名称】自然语言处理实习生（语义方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【所属小组】语义组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【人数】1~2人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【工作职责】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1、参与语义理解相关的研发工作；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2、参与部分项目的工程化工作；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【任职要求】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.熟悉机器学习、自然语言处理相关算法和模型；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.熟悉数据结构，有较强的算法能力；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3.具有java/c++开发相关经验，熟悉spring、mybatis等开发框架；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>4.熟悉mysql、mongodb、redis等数据库，了解linux；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>5.计算机及相关专业，本科以上学历；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>6.较强的逻辑思维，良好的沟通能力和团队合作惊声，责任心强；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>三、自然语言处理实习生（对话生成方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位名称】自然语言处理实习生（对话生成方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【所属小组】对话组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【人数】1~2人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职责描述】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.仔细阅读大量自然语言处理的会议论文&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.利用深度学习技术训练对话生成模型，以及模型的改进创新。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3.利用深度学习技术改进现有聊天对话系统，话题分类，话题推荐，大数据预测等等。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>4.深度学习与知识推理。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位要求】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.熟悉自然语言处理、深度学习的理论与实践&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.精通语义分析、主题发现、情感分析，对话生成等某一方面的工作。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3.对CNN、RNN、LSTM、Word2Vec等理解原理并能应用于实践。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>4.对相关领域最新动态持续关注。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>5.动手能力强。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>6.熟悉使用流行的深度学习框架。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>四、深度学习算法工程师（机器视觉方向）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位名称】深度学习算法工程师&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【所属小组】机器视觉组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【人数】1人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职责描述】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1. 负责研究和讨论深度学习相关的理论（20%精力）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2. 协助参与人脸识别、物体识别、OCR、肢体定位，手势识别等模块，图像语义，图片场景理解等研发（80%的精力）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位要求】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.数学或计算机相关学科本科以上学历，经过完整的机器学习或数据挖掘训练，（有完整的MOOC课程学习经历也可）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.掌握对机器视觉涉及到的机器学习方法，如CNN，adaboost, svm，并且有实战经验，有深度学习相关经验，至少接触过两种以上深度学习框架如MXNET, TENSORFLOW, CAFFE, TORCH, THEANO，Matlab DL toolbox&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3、对深度学习在某一领域的应用有深入思考和研究；如图像渲染，图像去模糊，强化学习，语义理解，对话生成，翻译等， 并有一定的论文阅读量。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>4.linux系统使用经验，熟悉C++和python，熟悉opencv，熟悉git&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>5.优秀的英文论文阅读能力，并能快速编程实现&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>6.良好的代码与文档风格&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>五、机器人3D视觉感知工程师&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位名称】机器人3D视觉感知工程师&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【所属小组】机器视觉组&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【人数】1人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职责描述】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1.负责研究和讨论机器学习和3D视觉相关的理论（20%精力）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2.协助参与基于3D视觉的肢体定位，手势识别、物体识别、视觉里程计、SLAM等模块的研发（80%的精力）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【职位要求】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1、理工科硕士以上学历，良好的数学基础，熟悉CV相关的几何问题&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2、了解图像处理常见概念&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3、熟悉机器学习领域常见概念和算法&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>4、熟悉C++语言，熟悉openCV库，有相关项目经验&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>5、有RGBD图像和点云处理相关经验&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>6、熟悉Point Cloud Library库者优先&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>7、熟悉Robitic Operating System常见概念，有相关项目经验者优先&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>8、对3D视觉及其在家用机器人的应用有强烈兴趣&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>注：工作要求掌握部分技能即可&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>待遇：200元左右/天+餐补&nbsp;&nbsp;&nbsp;&nbsp;弹性工作时间&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>邮箱：sundawei@uzoo.cn&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>微信：1035892905&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>电话：18233583332&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>【Leader说】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>我是图灵机器人的创始人&amp;CEO俞志晨，下面介绍下我们公司的“人才观”~&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>人才是本，产品是末。借用@纯银 的一句话，“选对人是一件比设好KPI更重要一百倍的事情。一百倍啊一百倍。”&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>“兴趣+主动+聪明”，比“当前的能力”要重要10倍。这样的人，在我们团队的成长会非常快，因为你可以直接来争取“想做的事情/职位”，公司一定竭尽所能的给你锻炼机会，只要你能对你自己负责，把事情真正做到位。但如果是“推一下、动一下”的人，我们一定不会要。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>不让人才受委屈。只要你真的能把事情做好，薪酬待遇、福利制度、团队氛围，甚至各种“个人关怀”，都会在公司力所能及的范围内提供给小伙伴——让你满意，让你的家人满意。我们团队，真的会让你感觉“暖暖的”。&nbsp;&nbsp;&nbsp;&nbsp;<br/>--<br/>白云偶尔会遮住蓝天，但蓝天永远在白云之上<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f005\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 106.39.200.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 10:36:19', 'PART', '0');
INSERT INTO `job` VALUES ('223147', 'QINGHUA', '【实习】【百度】搜索测试部 _实习开发测试工程师', '&nbsp;&nbsp;<br/>工作职责：&nbsp;&nbsp;<br/>-面向百度核心搜索引擎业务，提供系统测试角度的技术支持与改进，为搜索引擎各子系统业务测试人员提供技术基础，包括但不限于稳定性分析、异常测试、集成测试等；&nbsp;&nbsp;<br/>-通过机房监控、大数据分析等手段，为业务线RD和QA提供线上线下质量依据；&nbsp;&nbsp;<br/>-通过监控、反劫持、异常探测等手段，提升线上质量安全性；&nbsp;&nbsp;<br/>-通过工具、平台开发，为上述工作提供有力保障；&nbsp;&nbsp;<br/>-通过系统、代码的学习，加深产品理解，分析产品问题。&nbsp;&nbsp;<br/>招聘名额：&nbsp;&nbsp;<br/>-2人&nbsp;&nbsp;<br/>职位要求：&nbsp;&nbsp;<br/>-国家统招本科及以上计算机/互联网/通信或其他相关工科专业在校学生；&nbsp;&nbsp;<br/>-掌握C/C++、python、shell等语言，具备良好的开发能力；&nbsp;&nbsp;<br/>-对网页前端、数据库技术、linux有一定程度掌握的优先；&nbsp;&nbsp;<br/>-具备良好的沟通协作能力，工作细心、负责；&nbsp;&nbsp;<br/>-具备良好的学习能力、分析问题和处理问题的能力。&nbsp;&nbsp;<br/>其它要求：&nbsp;&nbsp;<br/>-18年毕业&nbsp;&nbsp;<br/>-至少实习半年，每周至少四天&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;<br/>诚待您的简历，请发送至邮箱：yangyanhong@baidu.com，liurong04@baidu.com，邮件标题请按此格式：应聘+职位名称+姓名。&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f006\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 220.181.38.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 10:38:47', 'PART', '0');
INSERT INTO `job` VALUES ('223149', 'QINGHUA', '【实习】【鹏扬基金】招聘SRE(面向未来的高素质运维)_不限专业', '&nbsp;&nbsp;<br/>【我们需要】<br/>SRE(Site Reliability Engineer)，即面向未来的高素质运维工程师，在团队中的地位高于开发工程师<br/>想了解SRE是什么，参见：<a target=\"_blank\" href=\"http://os.51cto.com/art/201507/485957.htm\">http://os.51cto.com/art/201507/485957.htm</a><br/>&nbsp;&nbsp;<br/>【<font color=\"#FF0000\"><b>选择我们的理由</b></font>】<br/>我们<font color=\"#FF0000\"><b>不限专业</b></font>，如果你渴望进入IT领域，渴望进入金融领域，请选择我们，我们会给你机会和发展空间。<br/>&nbsp;&nbsp;<br/>【你来能做什么】<br/>1、基于机器学习的量化研究平台的搭建和运维管理<br/>2、公募基金核心业务系统的运维管理<br/>3、未来将负责海量数据处理、分布式计算以及大规模机器学习相关系统的运维管理<br/>&nbsp;&nbsp;<br/>【我们希望你】<br/>1、有缜密的逻辑思维能力<br/>2、面对问题，有钻研精神和解决问题的决心<br/>3、工作积极主动、踏实、认真，有较强的责任心和服务意识，良好的表达及沟通协调能力<br/>4、17年毕业<br/>&nbsp;&nbsp;<br/>【我们能为你提供】<br/>1、实习工资<br/>2、优秀者可直接转正<br/>&nbsp;&nbsp;<br/>=====公司简介=====<br/>1、鹏扬基金管理有限公司（下称“鹏扬基金”）成立于2016年7月6日，是经中国证监会批准成立的全国性基金管理公司。公司注册资本和实收资本均为1亿元人民币，前华夏基金固定收益总监、来自于阳光私募的专业人士杨爱斌出资比例为55%，上海华石投资有限公司出资比例为45%。<br/>2、鹏扬基金核心团队原任职的“北京鹏扬投资管理有限公司”（下称“鹏扬投资”）以债券投资见长，投研团队凭借其优秀的投资管理能力和风险控制能力，屡获中证报“金牛私募管理公司奖（债券策略）”和上证报“金阳光三年期卓越私募公司奖（债券策略）”等诸多奖项，无论熊市牛市始终保持了优异稳健的业绩，为投资人获取了满意回报。目前该金牛私募投研团队的原班人马已平移至鹏扬基金，致力于为广大公众提供稳健的、普惠的金融服务。鹏扬基金在发展初期将发挥自身债券投研专长，专注于债券投资，致力于成为运营规范、投资稳健、信誉卓著、在中国资本市场有重要影响力的基金管理公司。<br/>&nbsp;&nbsp;<br/>=====办公地点=====<br/>北京市西城区复兴门外大街A2号中化大厦16层<br/>&nbsp;&nbsp;<br/>=====联系方式=====<br/>有意者请将简历发往邮箱： sundh@pyamc.com<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f003\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 124.65.183.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 10:55:51', 'PART', '0');
INSERT INTO `job` VALUES ('223151', 'QINGHUA', '【实习】【新浪微博】web前端开发实习生[开发测试框架方向]', '&nbsp;&nbsp;<br/>实习要求：<br/>面向18年毕业的在校学生（如果你是17年毕业的应届生，我们暂不提供校招名额,但可以给你锻炼机会）<br/>实习至少半年，每周至少4天<br/>&nbsp;&nbsp;<br/>工作内容：<br/>研究并开发基于HTML/css/Javascript的自动化测试框架<br/>&nbsp;&nbsp;<br/>任职要求：<br/>1. 熟悉Javascript，CSS语言，熟悉常见CSS使用技巧，熟悉Javascript对于数组，对象和DOM的原生API<br/>2. 有一定前端开发经验，最好能在面试时展示之前做过的项目。<br/>3. 熟悉Python<br/>4. 熟悉Linux shell<br/>5. 具备良好的服务意识、责任心、较强的学习能力、优秀的团队沟通与协作能力&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>公司福利和实习薪酬：<br/>公司班车+免费健身+转正机会+大牛mentor<br/>实习薪资：150-300/天<br/>公司地点：北京中关村软件园 新浪总部办公楼（西二旗城铁附近）<br/>&nbsp;&nbsp;<br/>请发送简历至 dongde#staff.weibo.com&nbsp;&nbsp;请将#替换为@<br/>简历标题写明 ：姓名+学校+哪年毕业+可实习时间段+一周实习几天<br/>&nbsp;&nbsp;<br/>--<br/><font class=\"f006\">※ 修改:&middot;linlone 于 Nov&nbsp;&nbsp;1 11:06:15 2016 修改本文&middot;[FROM: 61.135.152.*]</font><font class=\"f000\"><br/></font><font class=\"f000\"></font><font class=\"f003\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 61.135.152.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:05:05', 'PART', '0');
INSERT INTO `job` VALUES ('223154', 'QINGHUA', '【实习】长江证券创新业务部实习生招聘需求', '&nbsp;&nbsp;<br/>长江证券创新业务部固定收益团队实习招聘：<br/>&nbsp;&nbsp;<br/>◆公司介绍：<br/>长江证券是中国中西部最大的证券公司，A股第6家上市券商(股票代码000783.SZ)，经过多年来已经发展为全国性的大型全牌照券商;公司实力雄厚、功能齐全、管理规范、业绩突出，联合资信评定主体长期信用等级为AAA，为业内最高等级，且多次获得行业嘉奖;截至2015年底，注册资本达到55.29亿元，总资产和净资产分别达到996.25亿元和170.24亿元;营业部接近200家。<br/>&nbsp;&nbsp;<br/>◆部门介绍：<br/>长江证券创新业务部为公司试点创办的创新部门。部门固定收益投资管理团队来自国内著名资产管理机构，包括公募基金和券商资管等，且均曾在上述机构负责核心投研岗位，拥有着丰富的投资研究及组合管理经验。部门在利率债、信用债、可转债以及衍生品方面均配备业内领先专业人才，全方位匹配各类客户的固定收益投资管理需求。<br/>&nbsp;&nbsp;<br/>◆招聘流程：<br/>一、招聘面向非应届毕业生，拥有CFA、CPA、FRM者优先；<br/>二、经过简历筛选和若干轮面试后，我们将筛选出初步符合标准的实习生，进入实习考核期；<br/>三、每两个月为一个完整实习考核期，我们将为实习生制定严格的培养及考核计划，并提供高密度的内部培训，目标是培养高质量的债券二级市场买方人才。通过实习表现优异者，将滚动进入下一轮考核，并有机会得到正式留用机会。<br/>&nbsp;&nbsp;<br/>◆招聘职位及要求：<br/>一、信用研究岗：<br/>招聘人数：2~3人<br/>实习地点：北京<br/>实习时间：每周至少4天，以两个月为滚动考核期。<br/>基本要求：<br/>1、国内外重点院校硕士在读，非应届毕业生(十分优秀的本科生也可破格录取)。<br/>2、经济类相关专业。<br/>3、对于债券市场、信用分析、行业知识及财务知识有基本了解，有相关实习经验者为佳。<br/>基本职责：<br/>1、协助汇总每日一级及二级信用债相关信息。<br/>2、协助跟踪持仓债券信用状况。<br/>3、梳理行业内发债主体信用序列。<br/>4、研究跟踪行业信用利差情况。<br/>&nbsp;&nbsp;<br/>二、固定收益机构销售岗：<br/>招聘人数：2～3人<br/>实习地点：北京<br/>实习时间：每周至少4天，以两个月为考核期<br/>基本要求：<br/>1、国内外重点院校硕士在读，非应届毕业生(十分优秀的本科生也可破格录取)。<br/>2、专业不限、对固定收益资产管理业务有一定了解、对机构销售业务具备较高的从业热情。<br/>3、性格活泼外向，擅长沟通，有一定文体特长者优先。<br/>4、有相关客户资源或实习经验者为佳。<br/>基本职责：<br/>1、协助维护现有机构客户(银行、保险等)，为客户提供持续的产品信息和公司报告，协助客户完成交易，维护长期稳定的客户关系。<br/>2、协助开拓新增机构客户，负责收集、整理、完善客户信息。<br/>3、协助完成业务合同的沟通和订立。<br/>&nbsp;&nbsp;<br/>◆联系方式：<br/>有意者请将个人简历(邮件标题及简历名称以“学校-专业-姓名-岗位”为格式)和生活照发送至innovation_ficc@163.com；应聘者个人资料我们会妥善保密，谢谢。<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f001\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 117.101.162.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:14:46', 'PART', '0');
INSERT INTO `job` VALUES ('223155', 'QINGHUA', '【人民法院电子音像出版社招聘HR实习生】', '&nbsp;&nbsp;<br/>要求：全日制本科及以上学历的在校学生或应届毕业生，人力资源管理、工商管理等相关专业；每周至少3天；踏实可靠、责任心强、工作主动、思维活跃、耐心细致、具有团队合作精神。<br/>地址：前门地铁站附近<br/>福利：85元+20元餐补/每天&nbsp;&nbsp;&nbsp;&nbsp;~表现优秀有留用机会～<br/>PS：实习工作环境超赞，带的HR姐姐不仅人美且超Nice，可以接触HR领域的各个模块，能学到东西！在高院的大食堂吃饭简直是吃货福音~需尽快入职～<br/>有意向的小伙伴请把简历砸向fayuancbs@sina.com～<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f002\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 103.254.65.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:14:51', 'BOTH', '0');
INSERT INTO `job` VALUES ('223159', 'QINGHUA', '【实习】【今日头条】招聘Android开发实习生', '&nbsp;&nbsp;<br/>Android开发实习生<br/>&nbsp;&nbsp;<br/>工作职责：<br/>1.负责公司移动产品(今日头条、内涵段子等)的研发，编写高质量的代码&nbsp;&nbsp;<br/>2.和产品经理配合，深度参与手机产品需求讨论、功能定义等<br/>&nbsp;&nbsp;<br/>岗位要求：<br/>1.乐观积极，沟通良好&nbsp;&nbsp;<br/>2.热爱移动产品研发，愿意在移动开发领域深入钻研，并成为专家&nbsp;&nbsp;<br/>3.具有扎实的计算机相关基础，有Android开发经验优先&nbsp;&nbsp;<br/>4.对软件产品有强烈的责任心，具备良好的沟通能力和优秀的团队协作能力<br/>5.2018年毕业生优先，每周（工作日）保证4天实习时间<br/>&nbsp;&nbsp;<br/>实习薪资：300/天<br/>&nbsp;&nbsp;<br/>福利：免费三餐+水果零食<br/>公司地点：<br/>北京海淀区北三环西路43号中航广场&nbsp;&nbsp;<br/>邮件以【姓名-Android开发实习生-每周实习几天-可以实习几个月】为标题，发至 hr+an@bytedance.com<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f006\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 120.52.147.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:27:53', 'PART', '0');
INSERT INTO `job` VALUES ('223160', 'QINGHUA', '【实习】【今日头条】招聘iOS开发实习生', '&nbsp;&nbsp;<br/>iOS开发实习生<br/>&nbsp;&nbsp;<br/>工作职责：<br/>1.负责公司移动产品(今日头条、内涵段子等)的研发，编写高质量的代码&nbsp;&nbsp;<br/>2.和产品经理配合，深度参与手机产品需求讨论、功能定义等<br/>&nbsp;&nbsp;<br/>岗位要求：<br/>1.乐观积极，沟通良好&nbsp;&nbsp;<br/>2.热爱移动产品研发，愿意在移动开发领域深入钻研，并成为专家&nbsp;&nbsp;<br/>3.具有扎实的计算机相关基础，熟悉Objective-C或者Swift，有相关开发经验优先&nbsp;&nbsp;<br/>4.对软件产品有强烈的责任心，具备良好的沟通能力和优秀的团队协作能力<br/>5.2018年毕业生优先，每周（工作日）保证4天实习时间<br/>&nbsp;&nbsp;<br/>实习薪资：300/天<br/>福利：免费三餐+水果零食<br/>公司地点：<br/>北京海淀区北三环西路43号中航广场&nbsp;&nbsp;<br/>邮件以【姓名-iOS开发实习生-每周实习几天-可以实习几个月】为标题，发至 hr+ios@bytedance.com<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f006\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 120.52.147.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:28:03', 'PART', '0');
INSERT INTO `job` VALUES ('223161', 'QINGHUA', '【实习】【今日头条】招聘测试开发实习生', '&nbsp;&nbsp;<br/>测试开发实习生<br/>&nbsp;&nbsp;<br/>工作内容：<br/>参与头条产品的研发与测试过程：产品需求、功能设计，产品可测性设计与反馈，<br/>系统测试，性能测试，产品效果评估评测试等多方面，致力于产品的技术可靠性和用户体验。<br/>&nbsp;&nbsp;<br/>任职要求：<br/>1.计算机相关专业大三或研一、研二在读学生，也接受应届毕业生扎实的计算机基础，较强的算法能力<br/>2.有移动互联网测试经验，有自动化或白盒测试经验优先考虑<br/>3.热爱互联网，对互联网相关业务或技术充满好奇及热情&nbsp;&nbsp;<br/>4.快速的产品及业务学习能力，敏捷而周到的逻辑思维能力&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>公司福利和实习薪酬：<br/>免费三餐+下午茶+租房补贴+免费健身+转正机会+大牛mentor<br/>实习薪资：200-300/天<br/>&nbsp;&nbsp;<br/>公司地点：<br/>北京海淀区北三环西路43号中航广场<br/>邮件以【姓名-测试开发实习生-每周实习几天-可以实习几个月】为标题，发至 hr+te@bytedance.com<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f006\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 120.52.147.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:28:14', 'PART', '0');
INSERT INTO `job` VALUES ('223162', 'QINGHUA', '【实习】【今日头条】web后台开发实习生招聘', '&nbsp;&nbsp;<br/>今日头条web后台开发实习生招聘<br/>工作内容：<br/>今日头条服务端开发实习<br/>&nbsp;&nbsp;<br/>任职要求：<br/>1.计算机相关专业大三或研一、研二在读学生<br/>2.对于创新和解决有挑战性问题充满激情<br/>3.扎实的计算机基础，较强的算法能力<br/>4.熟练掌握一门编程语言<br/>&nbsp;&nbsp;<br/>公司福利和实习薪酬：<br/>免费三餐+下午茶+租房补贴+免费健身+转正机会+大牛mentor<br/>每周（工作日）工作至少4天，实习时间3-6个月<br/>实习薪资：200-300/天<br/>公司地点：<br/>北京海淀区北三环西路43号中航广场&nbsp;&nbsp;<br/>邮件以【姓名-web后台开发实习生-每周实习几天-可以实习几个月】为标题，发至 hr+se@bytedance.com<br/>&nbsp;&nbsp;<br/>关于我们：<br/>&nbsp;&nbsp;北京字节跳动科技有限公司（Bytedance Ltd.）创立于2012年3月，旗下产品Web网站www.toutiao.com，客户端今日头条、内涵段子、内涵社区等。公司目前已完成C轮融资。截至2016年2月，“今日头条”已经在为将近4亿的忠诚用户服务，每天有超过4000万的用户在头条上找到让他们了解世界、启发思考、开怀一笑的信息，并活跃地参与互动。<br/>“你关注的，才是头条” ———What We Do&nbsp;&nbsp;<br/>“今日头条”客户端是一款依靠强大机器跟算法支撑的，基于大数据挖掘的推荐引擎产品。 系统会采集海量的信息，通过数据挖掘，智能分析出每时每刻最热门最值得用户关注的资讯；同时，推荐引擎会对用户浏览、收藏、转发、评论新闻资讯的行为不断进行智能分析，再结合用户的阅读习惯、阅读时间、阅读位置、社交网络等多个维度，建立起个人用户模型，两者结合，为用户推荐个性化的信息。最后呈现一千个人眼前会有一千个“今日头条”的效果。我们要做的就是比你更懂你。&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;这么好的实习机会你还等什么，快点加入我们今日头条吧！<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f006\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 120.52.147.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:28:27', 'PART', '0');
