INSERT INTO `job` VALUES ('470689', 'QINGHUA', '【百度创业团队】招聘用户运营经理', '&nbsp;&nbsp;<br/>岗位职责：<br/>1、负责用户运营体系的建立、维护和完善，用户的留存和生命周期管理；<br/>2、以项目整体运营策略为方针，对细节运营工作进行管理、实施；<br/>3、搜集、分析用户行为数据和用户反馈，为活动运营、内容运营、市场推广等提供建议，协同配合提新增、活跃、留存、交易量等各种指标；<br/>4、负责利用个性化、精细化运营手段，提升用户体验，提升新用户转化投资率；<br/>5、负责反馈的用户建议和产品缺陷，推动产品质量的改善；<br/>6、了解相关领域动向，及时、准确地研究、监控、分析并提出对策。<br/>&nbsp;&nbsp;<br/>职位要求：<br/>1、具有两年及以上互联网用户运营经验；<br/>2、熟悉各种运营手段，具有独立运作用户运营体系的经验；<br/>3、具有良好的团队合作精神、优秀的沟通能力、强力的责任心和执行力；<br/>4、对互联网金融行业/金融领域具有浓厚兴趣。<br/>&nbsp;&nbsp;<br/>工作地址：北京市海淀区中关村soho<br/>&nbsp;&nbsp;<br/>有意者请发简历至recruit@innolife.cc<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f003\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 61.50.104.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 14:35:47', 'FULL', '0');
INSERT INTO `job` VALUES ('470691', 'QINGHUA', '【金山云】急招测试开发工程师', '&nbsp;&nbsp;<br/>地点：北京海淀上地金山软件大厦<br/>简历投递邮箱：wunan3@kingsoft.com（备注水木））<br/>薪资15k-30k<br/>岗位职责<br/>1. 参与云计算产品、网站产品的测试工作，包括产品功能测试、接口测试、性能测试等；<br/>2. 参与产品评审，能够快速了解需求，跟进项目；<br/>3. 根据项目需求和计划，能够设计和编写测试用例；<br/>4. 开发自动化测试代码和脚本；<br/>5. 执行测试用例，及时反映项目测试中的问题，详实描述BUG产生的过程，BUG现象，准确的定位并跟踪问题；<br/>6. 与项目相关人员就项目进度和问题进行沟通；<br/>7.根据测试过程编写相关测试报告。<br/>任职要求<br/>1. 正规高校计算机或相关专业本科以上学历，至少两到三年的测试工作经验；<br/>2. 熟悉软件测试理论和方法，熟悉互联网行业测试环境及相关技术；<br/>3. 熟悉Linux/Unix系统；<br/>4. 掌握一种或者多种开发语言（python，java优先），能熟练编写测试代码；<br/>5. 对软件测试有浓厚的兴趣和丰富的经验，有很强的分析能力和定位问题的能力；<br/>6. 有很强的质量意识，能严格地遵照测试流程规范。<br/>有如下经验者优先<br/>1. 云计算测试；<br/>2. 持续集成测试；<br/>3. 大数据处理测试。<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f003\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 114.255.44.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 14:36:36', 'FULL', '0');
INSERT INTO `job` VALUES ('470693', 'QINGHUA', '【乐视网】招聘高级JAVA开发/架构师', '&nbsp;&nbsp;<br/>工作职责：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>1、负责乐视全球内容开放平台的研发。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>2、负责乐视全球内容开放平台技术方案调研和性能优化；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>3、对代码质量负责，制定和review各种技术规范。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;