INSERT INTO `job` VALUES ('592624', 'QINGHUA', '请教下注销地税的麻烦事', '&nbsp;&nbsp;<br/>去注销地税，地税的和我说证件失效，已经没有专管员了，让去找所长，先得恢复正常。我问所长得罚款多少，跟我说得两千到几万不等，这太狠了，我一直无经营，零申报的小微公司，怎么罚款几千？这事能给他送点东西说说情吗？<br/>&nbsp;&nbsp;<br/>地税的说是因为我占了这个注册地址几年，意思就是导致这个注册地址没创造出税收来，所以怪我了<br/>&nbsp;&nbsp;<br/>--<br/>&nbsp;&nbsp;<br/><font class=\"f000\"></font><font class=\"f002\">※ 来源:&middot;水木社区 <a target=\"_blank\" href=\"http://www.newsmth.net\">http://www.newsmth.net</a>&middot;[FROM: 223.20.131.*]</font><font class=\"f000\"><br/></font><br/>', '2016-11-01 11:04:49', 'FULL', '0');
